import React from 'react';
import styled from 'styled-components';
const Gradienteffect = styled.button `
  --grad1: #03a9f4;
  --grad2: #f441a5;
  --grad3: #ffeb3b;
  --grad4: #03a9f4;
`;
const DivButtons = styled.div `
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
`;
const Buttons = styled.button `
  border-radius: 5px;
  margin: 10px;
  padding: 15px;
  font-family: Poppins;
  box-shadow: 0px 10px 10px rgba(74, 74, 74, 0.1);
  &:hover {
    transition: 250ms;
    padding: 13px;
    background-color: black;
    color: white;
    box-shadow: 0px 10px 10px rgba(74, 74, 74, 0.3);
    border: 2px solid transparent;
  }
`;


export default class Botones extends React.Component {
  render () {
    const { alerts } = this.props
    return (
      <DivButtons>
        <Buttons onClick={() => window.alert(alerts.m1)}>Módulo 1</Buttons>
        <Buttons onClick={() => window.alert(alerts.m2)}>Módulo 2</Buttons>
      </DivButtons>
    )
  }
}

// Esto lo exportamos para los tests
export { DivButtons, Buttons }
